import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Panel, PanelGroup } from "react-bootstrap";
import React from "react";
var daylist = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wednesday ",
  "Thursday",
  "Friday",
  "Saturday",
];

function MainComponent(props) {
  const [weatherData, setWeatherData] = useState(); //state of weather data

  let id = props.location; //get current location from app component

  //call api of particular location woth id
  const getData = async (id) => {
    const response = await axios.get(
      `http://api.openweathermap.org/data/2.5/weather?id=${id}&units=metric&APPID=baaff88971c49e8746e3c9e3262fc830`
    );
    setWeatherData(response.data);
  };

  //call data function before loading page
  useEffect(() => {
    getData(id);
  }, [id]);

  {
    console.log(weatherData);
  }

  //show data on UI if present
  if (weatherData) {
    return (
      <div className="panel">
        <div>
          <img
            src={`http://openweathermap.org/images/flags/${weatherData.sys.country.toLowerCase()}.png`}
          />
          <span> </span>
          <span>
            {weatherData.name},{weatherData.sys.country}
          </span>

          <p>
            feels like - {weatherData.main.feels_like},{" "}
            {weatherData.weather[0].description}
          </p>
          <span>
            <img
              src={`http://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png`}
            />
            {weatherData.main.temp - 273.15}&deg;C
          </span>
          <br />
        </div>

        <table id="customers">
          <tr>
            <td>
              <span>
                Expected weather from :{" "}
                <span id="wind">{weatherData.main.temp_min}</span> &deg;C
              </span>{" "}
              to{" "}
              <span>
                Max: <span id="wind">{weatherData.main.temp_max}-273.15</span>{" "}
                &deg;C
              </span>
            </td>
          </tr>
          <tr>
            <td>
              Humidity - <span id="humidity">{weatherData.main.humidity}</span>%
              <span>ðŸ’§</span>{" "}
              <span>Pressure : {weatherData.main.pressure} </span>
              <span>
                <span>Clouds : {weatherData.weather[0].description} </span>{" "}
              </span>
            </td>
          </tr>
          <tr>
            <td>
              <span>
                Wind: <span id="wind">{weatherData.wind.speed}</span> m/s
              </span>
            </td>
          </tr>
          <tr>
            <td>
              <span>
                Geo Location:{" "}
                <span id="wind">
                  {weatherData.coord.lat} , {weatherData.coord.lon}
                </span>
              </span>
            </td>
          </tr>
        </table>
      </div>
    );
  } else {
    return <div></div>;
  }
}

export default MainComponent;
