import {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import axios from "axios";
import {Panel, PanelGroup} from "react-bootstrap";
import React from "react";
var daylist = ["Sunday","Monday","Tuesday","Wednesday ","Thursday","Friday","Saturday"];

//component to show detail on click
function WeatherComponent(props) {
    const [wdata,setWdata] = useState(); //state to maintain data of city

    let {id} = useParams(); //get params from route

    //get data for city from city ID
    const getData = async(id)=>{
        const res =  await axios.get(`http://api.openweathermap.org/data/2.5/weather?id=${id}&units=metric&APPID=baaff88971c49e8746e3c9e3262fc830`)
        setWdata(res.data)
    }

    //get data call function before page loading
    useEffect(()=> {
        getData(id);
    },[id]);

    {console.log(wdata)}

    //if data is present show data on page
    if(wdata){
        return(
            <div className="panel">
                <div>
                    <img  src={`http://openweathermap.org/images/flags/${wdata.sys.country.toLowerCase()}.png`}/><span>     </span>
                    <span>{wdata.name},{wdata.sys.country}</span>

                    <p>feels like -  {wdata.main.feels_like}, {wdata.weather[0].description}</p>
                    <span>
                                <img src={`http://openweathermap.org/img/wn/${wdata.weather[0].icon}@2x.png`}/>
                        {wdata.main.temp}&deg;C
                            </span>
                    <br/>
                </div>

                <table id="customers">

                    <tr>
                        <td><span>Expected weather from : <span
                            id="wind">{wdata.main.temp_min}</span> &deg;C</span> to <span>Max: <span
                            id="wind">{wdata.main.temp_max}</span> &deg;C</span></td>

                    </tr>
                    <tr>
                        <td>Humidity - <span id="humidity">{wdata.main.humidity}</span>%
                            <span>ðŸ’§</span> <span>Pressure : {wdata.main.pressure} </span>
                            <span><span>Clouds : {wdata.weather[0].description} </span> </span></td>

                    </tr>
                    <tr>
                        <td><span>Wind: <span id="wind">{wdata.wind.speed}</span> m/s</span></td>

                    </tr>
                    <tr>
                        <td><span>Geo Location: <span id="wind">{wdata.coord.lat} , {wdata.coord.lon}</span></span></td>

                    </tr>
                </table>
            </div>
        )
    }
    else {
        return (
            <div></div>
        )
    };
}

export default WeatherComponent;
