import "./App.css";
import "./index.css";
import NavComponent from "./components/NavComponent";
import SearchListComponent from "./components/SearchListComponent";
import { useState } from "react";
import { Route } from "react-router-dom";
import { Switch } from "react-router-dom";
import React from "react";
//import WeatherComponent from "./components/WeatherComponent";
import MainComponent from "./components/MainComponent";

function App(props) {
  //state to maintain recently viewed cities
  const [recentlyViewed, setRecentlyViewed] = useState([]);

  //add recently view city in array
  const addRecentlyViewed = (id) => {
    if (!recentlyViewed.includes(id)) {
      recentlyViewed.push(id);
    }
    setRecentlyViewed([...recentlyViewed]);
  };

  //renders navbar component in app
  //switch between different routers
  return (
    <div className="App">
      {/*navbar component import*/}
      <NavComponent recentlyViewed={recentlyViewed}></NavComponent>
      <Switch>
        {/*switch between different routes in navbar component*/}
        <Route
          path="/city/:updateSearchId"
          children={
            <SearchListComponent addRecentlyViewed={addRecentlyViewed} />
          }
        ></Route>
        <Route path="/id/:id" children={<WeatherComponent />}></Route>
        {/*pass id for current location*/}
        <Route path="/" children={<MainComponent location={"6167865"} />} />
      </Switch>
    </div>
  );
}

export default App;
