import axios from "axios";
import { useState, useEffect } from "react";
//import { Button} from "react-bootstrap";
import { PanelGroup, Panel, Button } from "react-bootstrap";
import React from "react";
import { useParams } from "react-router-dom";

//component to render list of cities on search from navbar component

function SearchListComponent(props) {
  const [weatherData, setWeatherData] = useState([]); //state for weather data
  const [wpdata, setWpdata] = useState([]); //state for weather pagination data
  const [pageNumber, setPageNumber] = useState(1); //state for page number , initial value is one
  const [totalPages, setTotalPages] = useState(1); // state for total pages
  var daylist = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday ",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  const [currentDate, setCurrentDate] = useState(""); //state for setting date
  //const [sunset,setSunset] = useState("");
  const pageSize = 3;

  let { updateSearchId } = useParams(); //capture search id from navbar component

  //function for getting paginated data
  function getPaginatedData(res, size, max) {
    size = size || 10;
    max = max || 100;
    size = size > max ? max : size;
    var pages = Math.ceil(res.length / size),
      items = [];
    for (var p = 1; p <= pages; p++) {
      var start = Math.ceil(size * (p - 1));
      items.push(res.slice(start, start + size));
    }
    return items;
  }

  //function to get data from weather api
  const getData = async (cityId) => {
    const res = await axios.get(
      `http://api.openweathermap.org/data/2.5/find?units=metric&q=${cityId}&APPID=baaff88971c49e8746e3c9e3262fc830`
    );
    setWeatherData(res.data.list);
    setWpdata(getPaginatedData(res.data.list, 3, 3));
    setTotalPages(Math.ceil(wpdata.length / pageSize));
  };

  //call weather api and set data before rendering dom
  useEffect(() => {
    var current = new Date();
    var date = "";
    var date =
      "Date: " +
      current.getFullYear() +
      "/" +
      (current.getMonth() + 1) +
      "/" +
      current.getDate() +
      "...Time: " +
      current.getHours() +
      ":" +
      current.getMinutes();
    setCurrentDate(date);
    getData(updateSearchId);
  }, [updateSearchId]);

  //show data on UI
  const listData = (w, index) => {
    var sunrise = w.sys.sunrise;
    var sunset = w.sys.sunset;
    var sunrise_date = new Date(sunrise * 1000);
    sunrise = sunrise_date.toLocaleTimeString();
    var sunset_date = new Date(sunset * 1000);
    sunset = sunset_date.toLocaleTimeString();
    return (
      <div>
        <PanelGroup
          accordion
          id="accordion-uncontrolled-example"
          defaultActiveKey="2"
        >
          <Panel className="Panel" bsStyle="info" eventKey={w}>
            <Panel.Heading>
              <Panel.Title toggle>
                <div>
                  <img
                    src={`http://openweathermap.org/images/flags/${w.sys.country.toLowerCase()}.png`}
                  />
                  <span> </span>
                  <span>
                    {w.name},{w.sys.country}
                  </span>

                  <p>
                    feels like - {w.main.feels_like}, {w.weather[0].description}
                  </p>
                  <span>
                    <img
                      src={`http://openweathermap.org/img/wn/${w.weather[0].icon}@2x.png`}
                    />
                    {w.main.temp}&deg;C
                  </span>
                  <br />
                  <button
                    className="addbutton"
                    onClick={(e) => props.addRecentlyViewed(w.id)}
                  >
                    +
                  </button>
                </div>
              </Panel.Title>
            </Panel.Heading>
            <Panel.Body collapsible>
              <div>
                <table id="customers">
                  <tr>
                    <td>
                      <span>
                        Expected weather from :{" "}
                        <span id="wind">{w.main.temp_min}</span> &deg;C
                      </span>{" "}
                      to{" "}
                      <span>
                        Max: <span id="wind">{w.main.temp_max}</span> &deg;C
                      </span>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <span id="date">{currentDate}</span>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <span id="dayoftheweek">
                        {daylist[new Date().getDay()]}
                      </span>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <span>Sunrise: {sunrise}</span>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      Humidity - <span id="humidity">{w.main.humidity}</span>%
                      <span>ðŸ’§</span> <span>Pressure : {w.main.pressure} </span>
                      <span>
                        <span>Clouds : {w.weather[0].description} </span>{" "}
                      </span>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <span>
                        Wind: <span id="wind">{w.wind.speed}</span> m/s
                      </span>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <span>
                        Geo Location:{" "}
                        <span id="wind">
                          {w.coord.lat} , {w.coord.lon}
                        </span>
                      </span>
                    </td>
                  </tr>
                </table>
              </div>
            </Panel.Body>
          </Panel>
        </PanelGroup>
      </div>
    );
  };

  //function to show number of pagination buttons
  const paginationButton = (p, index) => {
    return (
      <Button
        bsStyle="primary"
        active={index + 1 === pageNumber}
        onClick={(e) => {
          setPageNumber(index + 1);
        }}
      >
        {index + 1}{" "}
      </Button>
    );
  };

  {
    console.log(weatherData);
  }
  {
    console.log(wpdata);
  }
  {
    console.log(pageNumber);
  }

  //renders list only if data is present
  if (weatherData.length > 0 && wpdata.length > 0) {
    return (
      <div>
        {Array.from(wpdata[pageNumber - 1]).map(listData)}
        {wpdata.map(paginationButton)}
      </div>
    );
  }
  //if data is not present show alert
  else {
    return (
      <div className="alert-danger">
        <mark>City you entered can't be found!!</mark>
      </div>
    );
  }
}

export default SearchListComponent;
